// Format validation results
const validationData = $input.item.json;

if (validationData.status !== 'success') {
  throw new Error('Validation failed: ' + (validationData.error || 'Unknown error'));
}

const changes = validationData.changes || {};
const signals = validationData.signals || [];

// Build change summary
const changeSummary = {
  dropped: changes.dropped || [],
  added: changes.added || [],
  score_changes: changes.score_changes || [],
  price_moves: changes.price_moves || []
};

// Format signals
const signalSummary = signals.slice(0, 10).map((s, idx) => {
  const factors = s.factors || {};
  return {
    rank: idx + 1,
    ticker: s.ticker,
    score: s.composite_score?.toFixed(3),
    price: s.price?.toFixed(2),
    momentum: {
      return_10d: factors.momentum?.return_10d?.toFixed(2),
      rsi: factors.momentum?.rsi_14?.toFixed(1)
    },
    volatility: {
      vol_20d: factors.volatility?.volatility_20d?.toFixed(1)
    },
    volume: {
      ratio: factors.volume?.volume_ratio?.toFixed(2)
    }
  };
});

return {
  scan_timestamp: validationData.timestamp,
  execution_time: validationData.execution_time,
  stats: validationData.stats,
  changes: changeSummary,
  signals: signalSummary,
  has_changes: validationData.notify
};

